SELECT recommendation_month, theme, target_audience, title, subject_category, author, publisher, publication_year, isbn, call_number FROM recommended_books WHERE 1=1
AND recommendation_month LIKE CONCAT(#{year}, '%')
AND target_audience = #{target}
AND ( title LIKE CONCAT('%', #{keyword}, '%') OR author LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY LEFT(recommendation_month, 4) DESC, CASE target_audience WHEN '유아' THEN 1 WHEN '초등저학년' THEN 2 WHEN '초등고학년' THEN 3 WHEN '청소년' THEN 4 ELSE 99 END;
